import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  ScrollView,
  Alert,
  Animated,
  useWindowDimensions,
  KeyboardAvoidingView,
  Platform
} from 'react-native';
import { useNavigation, NavigationProp } from '@react-navigation/native';
import { useAuth } from '../contexts/AuthContext';
import { Ionicons, FontAwesome5, MaterialCommunityIcons } from '@expo/vector-icons';

type RootStackParamList = {
  Welcome: undefined;
  Login: undefined;
  Profile: undefined;
};

const LoginScreen: React.FC = () => {
  const { login, isLoading, authState } = useAuth();
  const { width } = useWindowDimensions();
  const isMobile = width < 768;
  const isTablet = width >= 768 && width < 1024;
  const isDesktop = width >= 1024;

  const navigation = useNavigation<NavigationProp<RootStackParamList>>();

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  // Animaciones
  const fadeAnim = React.useRef(new Animated.Value(0)).current;
  const slideAnim = React.useRef(new Animated.Value(50)).current;
  const pulseAnim = React.useRef(new Animated.Value(1)).current;
  const glowAnim = React.useRef(new Animated.Value(0)).current;

  // Colores tema Netflix/Spotify Neon
  const neonColors = {
    netflixRed: '#E50914',
    spotifyGreen: '#1DB954',
    neonBlue: '#00D9FF',
    neonPurple: '#8B5CF6',
    darkBg: '#0A0A0A',
    cardBg: '#141414',
    textPrimary: '#FFFFFF',
    textSecondary: '#B3B3B3',
  };

  // ‚úÖ CORREGIDO: NAVEGACI√ìN AUTOM√ÅTICA despu√©s del login exitoso
  useEffect(() => {
    if (authState.isAuthenticated) {
      console.log('‚úÖ Usuario autenticado, navegando a Profile...');
      // Usar setTimeout para evitar problemas de actualizaci√≥n de estado
      setTimeout(() => {
        navigation.navigate('Profile');
      }, 100);
    }
  }, [authState.isAuthenticated, navigation]);

  useEffect(() => {
    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 800,
        useNativeDriver: true,
      }),
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 800,
        useNativeDriver: true,
      }),
      Animated.loop(
        Animated.sequence([
          Animated.timing(pulseAnim, {
            toValue: 1.08,
            duration: 2000,
            useNativeDriver: true,
          }),
          Animated.timing(pulseAnim, {
            toValue: 1,
            duration: 2000,
            useNativeDriver: true,
          }),
        ])
      ),
      Animated.loop(
        Animated.sequence([
          Animated.timing(glowAnim, {
            toValue: 1,
            duration: 2500,
            useNativeDriver: true,
          }),
          Animated.timing(glowAnim, {
            toValue: 0,
            duration: 2500,
            useNativeDriver: true,
          }),
        ])
      ),
    ]).start();
  }, []);

  // Calcular dimensiones din√°micas responsivas - MEJORADO PARA FORMULARIO M√ÅS ANCHO
  const getCardWidth = () => {
    if (isMobile) return '95%';
    if (isTablet) return '80%'; // Reducido para dar m√°s espacio al formulario
    return '60%'; // Reducido significativamente para desktop
  };

  const getMaxWidth = () => {
    if (isDesktop) return 1200; // Aumentado considerablemente para desktop
    if (isTablet) return 800; // Aumentado para tablet
    return 480;
  };

  const getCardPadding = () => {
    if (isMobile) return 24;
    if (isTablet) return 36;
    return 40; // Reducido ligeramente para dar m√°s espacio al contenido
  };

  const getTitleSize = () => {
    if (isMobile) return 28;
    if (isTablet) return 32;
    return 36; // Ajustado para desktop
  };

  const getSubtitleSize = () => {
    if (isMobile) return 15;
    if (isTablet) return 16;
    return 18; // Ajustado para desktop
  };

  const getInputPadding = () => {
    if (isMobile) return 16;
    if (isTablet) return 18;
    return 20; // Ajustado para desktop
  };

  const getInputFontSize = () => {
    if (isMobile) return 16;
    if (isTablet) return 17;
    return 18; // Ajustado para desktop
  };

  const getButtonPadding = () => {
    if (isMobile) return 16;
    if (isTablet) return 18;
    return 20; // Ajustado para desktop
  };

  const getButtonFontSize = () => {
    if (isMobile) return 17;
    if (isTablet) return 18;
    return 19; // Ajustado para desktop
  };

  const getLabelSize = () => {
    if (isMobile) return 15;
    if (isTablet) return 16;
    return 17; // Ajustado para desktop
  };

  const getIconSize = () => {
    if (isMobile) return 36;
    if (isTablet) return 42;
    return 48; // Ajustado para desktop
  };

  // MEJORADO: Ancho del formulario interno significativamente aumentado
  const getFormWidth = () => {
    if (isDesktop) return '95%'; // Aumentado al 95% para desktop
    if (isTablet) return '98%'; // Aumentado al 98% para tablet
    return '100%'; // Mantenido en 100% para mobile
  };

  const getFormGap = () => {
    if (isDesktop) return 24;
    if (isTablet) return 20;
    return 18;
  };

