  const getMinHeight = () => {
    if (isDesktop) return 600;
    if (isTablet) return 500;
    return 0; // No aplicar altura m√≠nima en mobile
  };

  const handleNavigateToWelcome = () => {
    console.log('üîÑ Navegando a WelcomeScreen...');
    navigation.navigate('Welcome');
  };

  // CORREGIDO: Manejo mejorado del login
  const handleLogin = async () => {
    console.log('üîê Intentando login con:', { username });
    
    if (!username || username.trim() === '' || !password || password.trim() === '') {
      Alert.alert('Error', 'Por favor ingresa usuario y contrase√±a');
      return;
    }

    try {
      await login(username.trim(), password.trim());
      console.log('‚úÖ Login completado, navegaci√≥n autom√°tica a Profile');
      // La navegaci√≥n se maneja autom√°ticamente en el useEffect
    } catch (error) {
      console.error('‚ùå Error en login:', error);
      Alert.alert('Error de Autenticaci√≥n', 'Usuario o contrase√±a incorrectos');
    }
