                  />
                </Animated.View>
                <Text style={[styles.title, { 
                  fontSize: getTitleSize(),
                  color: neonColors.textPrimary 
                }]}>
                  Iniciar Sesi√≥n
                </Text>
                <Text style={[styles.subtitle, { 
                  fontSize: getSubtitleSize(),
                  color: neonColors.spotifyGreen 
                }]}>
                  Ingresa tus credenciales de estudiante
                </Text>
              </View>

              {/* Divider */}
              <View style={styles.dividerContainer}>
                <View style={[styles.dividerLine, { backgroundColor: neonColors.spotifyGreen }]} />
                <MaterialCommunityIcons name="shield-key" size={24} color={neonColors.spotifyGreen} />
                <View style={[styles.dividerLine, { backgroundColor: neonColors.spotifyGreen }]} />
              </View>

              {/* Formulario - MEJORADO: Formulario significativamente m√°s ancho */}
              <View style={[
                styles.form, 
                { 
                  width: getFormWidth(),
                  gap: getFormGap(),
                  alignSelf: 'center'
                }
              ]}>
                {/* Campo Usuario */}
                <View style={styles.inputGroup}>
                  <View style={styles.labelContainer}>
                    <Ionicons name="person" size={18} color={neonColors.neonBlue} />
                    <Text style={[styles.label, { 
                      fontSize: getLabelSize(),
                      color: neonColors.neonBlue 
                    }]}>
                      Usuario *
                    </Text>
                  </View>
                  <TextInput
                    style={[
                      styles.input,
                      { 
                        padding: getInputPadding(),
                        fontSize: getInputFontSize(),
                        borderColor: neonColors.neonBlue,
                        color: neonColors.textPrimary
                      }
                    ]}
                    placeholder="Ingresa tu usuario"
                    placeholderTextColor={neonColors.textSecondary}
                    value={username}
                    onChangeText={(text) => setUsername(text)}
                    autoCapitalize="none"
                    autoCorrect={false}
                    returnKeyType="next"
                    editable={!isLoading}
                  />
                </View>

                {/* Campo Contrase√±a */}
                <View style={styles.inputGroup}>
                  <View style={styles.labelContainer}>
                    <Ionicons name="lock-closed" size={18} color={neonColors.neonBlue} />
                    <Text style={[styles.label, { 
                      fontSize: getLabelSize(),
                      color: neonColors.neonBlue 
                    }]}>
                      Contrase√±a *
                    </Text>
                  </View>
                  <View style={styles.passwordContainer}>
                    <TextInput
                      style={[
                        styles.input,
                        styles.passwordInput,
                        { 
                          padding: getInputPadding(),
                          fontSize: getInputFontSize(),
                          borderColor: neonColors.neonBlue,
                          color: neonColors.textPrimary
                        }
                      ]}
                      placeholder="Ingresa tu contrase√±a"
                      placeholderTextColor={neonColors.textSecondary}
                      value={password}
                      onChangeText={(text) => setPassword(text)}
                      secureTextEntry={!showPassword}
                      autoCapitalize="none"
                      autoCorrect={false}
                      returnKeyType="done"
                      onSubmitEditing={handleLogin}
                      editable={!isLoading}
                    />
                    <TouchableOpacity
                      style={[
                        styles.eyeButton,
                        { top: getInputPadding() }
                      ]}
                      onPress={() => setShowPassword(!showPassword)}
                      disabled={isLoading}
                    >
                      <Ionicons 
                        name={showPassword ? "eye-off" : "eye"} 
                        size={22} 
                        color={neonColors.neonBlue} 
                      />
                    </TouchableOpacity>
                  </View>
                </View>

                {/* Bot√≥n de Login */}
                <Animated.View style={{ opacity: glowOpacity }}>
                  <TouchableOpacity
                    style={[
                      styles.loginButton,
                      {
                        paddingVertical: getButtonPadding(),
                        backgroundColor: neonColors.spotifyGreen,
                        shadowColor: neonColors.spotifyGreen,
                      },
                      isLoading && styles.buttonDisabled,
                    ]}
                    onPress={handleLogin}
                    disabled={isLoading}
                    activeOpacity={0.8}
                  >
                    <View style={styles.buttonContent}>
                      {!isLoading && (
                        <MaterialCommunityIcons name="login" size={22} color="#000000" />
                      )}
                      <Text style={[
                        styles.loginButtonText,
                        { fontSize: getButtonFontSize() }
                      ]}>
                        {isLoading ? 'Autenticando...' : 'Iniciar Sesi√≥n'}
                      </Text>
                      {isLoading ? (
                        <Ionicons name="refresh" size={20} color="#000000" />
                      ) : (
                        <Ionicons name="arrow-forward" size={20} color="#000000" />
                      )}
                    </View>
                  </TouchableOpacity>
                </Animated.View>

                {/* Estado de carga */}
                {isLoading && (
                  <View style={[styles.loadingContainer, { borderColor: neonColors.spotifyGreen }]}>
                    <MaterialCommunityIcons name="shield-sync" size={20} color={neonColors.spotifyGreen} />
                    <Text style={[styles.loadingText, { color: neonColors.spotifyGreen }]}>
                      Autenticando con Keycloak...
                    </Text>
                  </View>
                )}

                {/* Bot√≥n para volver a Welcome */}
                <TouchableOpacity 
                  style={[
                    styles.backButton,
                    { 
                      borderColor: neonColors.neonPurple,
                      backgroundColor: `${neonColors.neonPurple}10`
                    }
                  ]}
                  onPress={handleNavigateToWelcome}
                  disabled={isLoading}
                >
                  <View style={styles.buttonContent}>
                    <Ionicons name="arrow-back" size={20} color={neonColors.neonPurple} />
                    <Text style={[styles.backButtonText, { 
                      color: neonColors.neonPurple,
                      fontSize: getLabelSize() 
                    }]}>
                      Volver a Inicio
                    </Text>
                  </View>
                </TouchableOpacity>

                {/* Informaci√≥n del sistema */}
                <View style={[styles.infoContainer, { borderColor: neonColors.neonBlue }]}>
                  <View style={styles.infoHeader}>
                    <FontAwesome5 name="react" size={16} color={neonColors.spotifyGreen} />
                    <Text style={[styles.infoTitle, { color: neonColors.spotifyGreen }]}>
                      Autenticaci√≥n Segura
                    </Text>
                  </View>
                  <View style={styles.infoGrid}>
                    <View style={styles.infoItem}>
                      <Ionicons name="server" size={12} color={neonColors.neonBlue} />
                      <Text style={[styles.infoText, { color: neonColors.textSecondary }]}>
                        localhost:8080
                      </Text>
                    </View>
                    <View style={styles.infoItem}>
                      <MaterialCommunityIcons name="shield-account" size={12} color={neonColors.neonBlue} />
                      <Text style={[styles.infoText, { color: neonColors.textSecondary }]}>
                        Realm: test-realm
                      </Text>
                    </View>
                    <View style={styles.infoItem}>
                      <Ionicons name="key" size={12} color={neonColors.neonBlue} />
                      <Text style={[styles.infoText, { color: neonColors.textSecondary }]}>
                        Client: react-client
                      </Text>
                    </View>
                  </View>
                </View>
              </View>
            </Animated.View>
          </View>
        </ScrollView>
      </KeyboardAvoidingView>
    </View>
  );
};

