import React from 'react';
import { StatusBar } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { AuthProvider, useAuth } from './src/contexts/AuthContext';
import WelcomeScreen from './src/screens/WelcomeScreen';
import LoginScreen from './src/screens/LoginScreen';
import RegisterScreen from './src/screens/RegisterScreen'; // ✅ NUEVA IMPORTACIÓN
import ProfileScreen from './src/screens/ProfileScreen';

// ✅ ACTUALIZADO: Agregar Register a los tipos de navegación
export type RootStackParamList = {
  Welcome: undefined;
  Login: undefined;
  Register: undefined; // ✅ NUEVA PANTALLA
  Profile: undefined;
};

const Stack = createNativeStackNavigator<RootStackParamList>();

// Componente que maneja la navegación basada en autenticación
const AppNavigator: React.FC = () => {
  const { authState, isLoading } = useAuth();

  // Mostrar pantalla de carga mientras verifica autenticación
  if (isLoading) {
    return (
      <Stack.Navigator screenOptions={{ headerShown: false }}>
        <Stack.Screen name="Welcome" component={WelcomeScreen} />
      </Stack.Navigator>
    );
  }

  return (
    <Stack.Navigator 
      screenOptions={{ 
        headerShown: false,
        animation: 'slide_from_right',
        gestureEnabled: true,
      }}
    >
      {authState.isAuthenticated ? (
        // ✅ USUARIO AUTENTICADO: Solo puede ver Profile
        <Stack.Screen name="Profile" component={ProfileScreen} />
      ) : (
        // ❌ USUARIO NO AUTENTICADO: Puede ver Welcome, Login y Register
        <>
          <Stack.Screen name="Welcome" component={WelcomeScreen} />
          <Stack.Screen name="Login" component={LoginScreen} />
          <Stack.Screen name="Register" component={RegisterScreen} /> {/* ✅ NUEVA PANTALLA */}
        </>
      )}
    </Stack.Navigator>
  );
};

const App: React.FC = () => {
  return (
    <AuthProvider>
      <StatusBar 
        barStyle="light-content" 
        backgroundColor="transparent"
        translucent={true}
      />
      <NavigationContainer>
        <AppNavigator />
      </NavigationContainer>
    </AuthProvider>
  );
};

export default App;
